 // DEPENDENCIES
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';
import { HttpClient } from '@angular/common/http';

// CONFIG
import { environment } from '../../../environments/environment';

// MODEL
import { Schema } from '../../domain/datastructure_db/schema';

/**
 * THIS SERVICE MAKE HTTP REQUEST TO SERVER, FOR CUSTOMIZE IT EDIT ../Schema.service.ts
 */

/*
 * SCHEMA DB Schema
 *
	{
		Name: {
			type: 'String',
			required : true
		},
		//RELATIONS
		//EXTERNAL RELATIONS
		Project: {
			type: Schema.ObjectId,
			ref : "Schema"
		},
	}
 *
 */
@Injectable()
export class SchemaBaseService {

    contextUrl: string = environment.endpoint + '/schema';
    constructor(
        protected http: HttpClient
        ) { }

    // CRUD METHODS

    /**
    * SchemaService.findByProject
    *   @description CRUD ACTION findByProject
    *   @param Objectid key Id of model to search for
    *
    */
    findByProject(id: string): Observable<Schema[]> {
        return this.http
            .get<Schema[]>(this.contextUrl + '/findByProject/' + id)
            .pipe(
                map(response => response)
            );
    }


    // Custom APIs

}
